div.script:
document.addEventListener("DOMContentLoaded", function() {
    var urlButton = document.getElementById("urlButton");
    var mapUrl = document.getElementById("partner_map");
    var oUrlInput = document.getElementById("o_url_input");

    // Check if map_url exists
    if (mapUrl) {
        oUrlInput.style.display = "none";
    }

    urlButton.addEventListener("click", function() {
        if (mapUrl) {
            mapUrl.style.display = "none";
            oUrlInput.style.display = "block";
        } else {
            oUrlInput.style.display = "block";
        }
    });
});

// Get references to the select and input elements
var deliveryStatusSelect =
document.getElementById("delivery_status_select");
var dateDeliveryField =
document.getElementById("date_delivery_field");

// Add an event listener to the select element
deliveryStatusSelect.addEventListener("change", function () {
if (deliveryStatusSelect.value == "not_home") {
    dateDeliveryField.style.display = "block";
} else {
    dateDeliveryField.style.display = "none";
}
});

template.script:
$(document).ready(function() {
// Get the value from the span element
var totalDue = parseFloat($("#prev_due span").text());

// Format the value to two decimal places
var formattedTotalDue = totalDue.toFixed(2);

// Update the span with the formatted value
$("#prev_due span").text(formattedTotalDue);
});

$(document).ready(function() {
// Get the value from the span element
var totalPay = parseFloat($("#total_payable span").text());

// Format the value to two decimal places
var formattedTotalPay = totalPay.toFixed(2);

// Update the span with the formatted value
$("#total_payable span").text(formattedTotalPay);
});

// JavaScript to handle the click event
document.addEventListener("DOMContentLoaded", function () {
const tdElement = document.querySelector('td.text-end');
tdElement.addEventListener("click", function () {
    tdElement.classList.add("clicked");
    });
});
